import React from 'react';
import Select from 'react-select'
import logo from './Yorkshire-Water-logo.jpg';
import arrow_1 from './yw-arrow-1.svg';
import arrow_2 from './yw-arrow-2.svg';
import './App.css';
import {Button} from 'react-bootstrap';
import {Tab} from 'react-bootstrap';
import {Tabs} from 'react-bootstrap';
import {DropdownButton} from 'react-bootstrap';
import {Dropdown} from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';


const options = [
  { value: 'Polymer 1 Cost', label: 'Polymer 1 Cost' },
  { value: 'Polymer 2 Cost', label: 'Polymer 2 Cost' },
  { value: 'KWH Cost', label: 'KWH Cost' },
  { value: 'KWH Value', label: 'KWH Value' },
  { value: 'uel CostF', label: 'uel Cost' },
  { value: 'Sludge Production Modifier', label: 'Sludge Production Modifier' },
  { value: 'Sludge Throughput Modifier', label: 'Sludge Throughput Modifier' }
]

const styles = {
  container: base => ({
    ...base,
    flex: 1
  })
};


function App() {
  return (
    <Tabs defaultActiveKey="profile" id="uncontrolled-tab-example">
      <Tab eventKey="profile" title="Standard Modelling">
    <div>
    <div>
        <img src={logo} alt="logo" width="400" height="200"/>
        <h1 class = "page-title">Sludge Modelling Tool</h1>
    </div>

    <div class = "top-container">
    <input type="file" id="files" name="file" onclick="displayProcess(0)"/>

        <DropdownButton id="dropdown-basic-button" title="Output Type">
          <Dropdown.Item href="#/csv">csv</Dropdown.Item>
          <Dropdown.Item href="#/pdf">PDF</Dropdown.Item>
        </DropdownButton>
    </div>

    <style type="text/css">
    {`.btn-xxl {
      font-size: 2rem;
      height: 60%;
      width: 25%;

    }
    `}
  </style>

    <div class = "central-container">
        <Button variant="outline-primary" size="xxl">
          Input Static Data
        </Button>

        <img src={arrow_1} alt="arrow_1" width="300" height="100" class = "arrow"/>

        <Button variant="outline-primary" size="xxl">
          Run Model
        </Button>

        <img src={arrow_2} alt="arrow_2" width="260" height="100" class = "arrow-2"/>

        <Button variant="outline-primary" size="xxl">
          Get Results
        </Button>
    </div>

    <div class = "lower-container"> 
          <Button variant="outline-primary" size="xxl">
          Update Static Data
          </Button>
    </div>
    </div>
    </Tab>
    <Tab eventKey="contact" title="Scenario Modelling">
    <div>
        <img src={logo} alt="logo" width="400" height="200"/>
        <h1 class = "page-title">Sludge Modelling Tool</h1>
    </div>

    <div class = "select-container">

    <Select options={options} styles={styles}/>



    </div>




    </Tab>
    </Tabs>


);
}

export default App;
