import React from 'react';
import logo from './Yorkshire-Water-logo.jpg';
import arrow_1 from './yw-arrow-1.svg';
import arrow_2 from './yw-arrow-2.svg';
import './App.css';
import {Button} from 'react-bootstrap';
import {Tab} from 'react-bootstrap';
import {Tabs} from 'react-bootstrap';
import {DropdownButton} from 'react-bootstrap';
import {Dropdown} from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

function App() {
  return (
    <Tabs defaultActiveKey="profile" id="uncontrolled-tab-example">
      <Tab eventKey="profile" title="Model">
    <div>
    <div>
        <img src={logo} alt="logo" width="400" height="200"/>
        <h1 class = "page-title">Sludge Modelling Tool</h1>
    </div>

    <div class = "top-container">
    <input type="file" id="files" name="file" onclick="displayProcess(0)"/>

        <DropdownButton id="dropdown-basic-button" title="Output Type">
          <Dropdown.Item href="#/csv">csv</Dropdown.Item>
          <Dropdown.Item href="#/pdf">PDF</Dropdown.Item>
        </DropdownButton>
    </div>

    <div>

    <style type="text/css">
    {`.btn-xxl {
      padding: 1rem 1.5rem;
      font-size: 1.5rem;
    }
    `}
  </style>

        <Button variant="outline-primary" styles = {{padding: "2rem 3rem", fontSize: "2rem"}} size="xxl" class = "central-container">
          Input Static Data
        </Button>
    </div>

        <img src={arrow_1} alt="arrow_1" width="300" height="100" class = "central-container-2"/>

    <div>
        <Button variant="outline-primary" styles = {{padding: "2rem 3rem", fontSize: "2rem"}} size="xxl" class = "central-container-3">
          Run Model
        </Button>
    </div>

        <img src={arrow_2} alt="arrow_1" width="300" height="100" class = "central-container-3"/>

    <div>  
        <Button variant="outline-primary" styles = {{padding: "2rem 3rem", fontSize: "2rem"}} size="xxl" class = "central-container-3">
          Get Results
        </Button>
    </div>

    <div class = "lower-container" styles = {{}}> 
          <Button variant="outline-primary" styles = {{padding: "2rem 3rem", fontSize: "2rem"}} size="xxl">
          Update Static Data
          </Button>
    </div>
    </div>
    </Tab>
    <Tab eventKey="contact" title="Parameters">
    <div>
        <img src={logo} alt="logo" width="400" height="200"/>
    </div>
    </Tab>

    </Tabs>


);
}

export default App;
